import React, { useState } from 'react';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [summaries, setSummaries] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/pdf') {
      setFile(selectedFile);
      setError(null);
    } else {
      setFile(null);
      setError('Por favor, selecione um arquivo PDF válido.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setError('Por favor, selecione um arquivo PDF válido.');
      return;
    }

    const formData = new FormData();
    formData.append('pdf', file);

    setLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:5000/api/upload', {
        method: 'POST',
        body: formData,
      });
      
      if (!response.ok) {
        throw new Error('Erro ao processar o documento');
      }
      
      const data = await response.json();
      setSummaries(data.summaries);
    } catch (error) {
      console.error('Erro ao enviar o arquivo:', error);
      setError(error.message || 'Erro ao processar o documento. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Extrator de Resultados de Exames</h1>
        <p className="subtitle">Faça upload de um PDF de exames de sangue para extrair os resultados</p>
      </header>
      <main>
        <div className="uploader-container">
          <form onSubmit={handleSubmit}>
            <div className="file-input-container">
              <input
                type="file"
                id="pdf-upload"
                onChange={handleFileChange}
                accept="application/pdf"
              />
              <label htmlFor="pdf-upload" className="file-label">
                {file ? file.name : 'Escolher arquivo PDF de exames'}
              </label>
            </div>
            <button 
              type="submit" 
              className="upload-button"
              disabled={!file}
            >
              Extrair Resultados
            </button>
          </form>
        </div>

        {loading && <p className="loading">Processando o documento, por favor aguarde...</p>}
        {error && <p className="error">Erro: {error}</p>}

        <div className="summary-container">
          {summaries.length === 0 ? (
            <p className="empty-message">Os resultados dos exames aparecerão aqui.</p>
          ) : (
            <>
              <h2>Resultados dos Exames</h2>
              <div className="summaries-list">
                {summaries.map((summary, index) => (
                  <div key={index} className="summary-card">
                    <h3>Página {summary.page}</h3>
                    <div className="summary-content">
                      {summary.content.split("\n").map((line, i) => (
                        line.trim() ? <p key={i}>{line}</p> : null
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </>
          )}
        </div>
      </main>
      <footer>
        <p>© 2025 - Extrator de Resultados de Exames</p>
      </footer>
    </div>
  );
}

export default App;
