// client/src/App.js
import React, { useState } from 'react';
import PdfUploader from './components/PdfUploader';
import SummaryViewer from './components/SummaryViewer';
import './App.css';

function App() {
  const [summaries, setSummaries] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSummariesUpdate = (newSummaries) => {
    setSummaries(newSummaries);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Resumidor de PDF</h1>
      </header>
      <main>
        <PdfUploader 
          onSummariesUpdate={handleSummariesUpdate} 
          setLoading={setLoading}
          setError={setError}
        />
        {loading && <p className="loading">Processando o documento, por favor aguarde...</p>}
        {error && <p className="error">Erro: {error}</p>}
        <SummaryViewer summaries={summaries} />
      </main>
    </div>
  );
}

export default App;
