// App.js melhorado - VersÃ£o premium com animaÃ§Ãµes avanÃ§adas e UX otimizada
import React, { useState, useRef, useEffect, useCallback } from 'react';
import './App.css';
import ErrorHandler from './components/ErrorHandler';

// FunÃ§Ã£o para determinar a URL da API baseada no ambiente
function getApiUrl() {
  // Em desenvolvimento (local), usa localhost
  if (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1') {
    return 'http://localhost:5000';
  }
  
  // Em produÃ§Ã£o, usa o domÃ­nio do Vercel para o backend
  return 'https://server-theta-murex.vercel.app';
}

// URL da API - detecta automaticamente ambiente local ou produÃ§Ã£o
const API_URL = getApiUrl();

function App() {
  const [files, setFiles] = useState([]);
  const [summaries, setSummaries] = useState([]);
  const [patientName, setPatientName] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [apiStatus, setApiStatus] = useState(null);
  const [uploadStatus, setUploadStatus] = useState(null);
  const [processingStage, setProcessingStage] = useState(null);
  const [currentProcessingFile, setCurrentProcessingFile] = useState(null);
  const [processedFiles, setProcessedFiles] = useState(0);
  const [totalFiles, setTotalFiles] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);
  const [showCelebration, setShowCelebration] = useState(false);
  const [dragOver, setDragOver] = useState(false);
  const textAreaRef = useRef(null);
  const fileInputRef = useRef(null);
  const uploadContainerRef = useRef(null);

  // FunÃ§Ã£o de debounce para otimizar performance
  const useDebounce = (callback, delay) => {
    const timeoutRef = useRef(null);
    
    return useCallback((...args) => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
      timeoutRef.current = setTimeout(() => callback(...args), delay);
    }, [callback, delay]);
  };

  // Verificar status da API ao carregar com animaÃ§Ã£o
  useEffect(() => {
    const checkApiStatus = async () => {
      setApiStatus({ status: 'checking', message: 'Verificando conexÃ£o...', url: API_URL });
      
      try {
        await new Promise(resolve => setTimeout(resolve, 800)); // Pequeno delay para UX
        const response = await fetch(`${API_URL}/api/health`);
        
        if (response.ok) {
          const data = await response.json();
          setApiStatus({
            status: 'online',
            env: data.env,
            url: API_URL
          });
          console.log(`ðŸš€ API conectada: ${API_URL} (${data.env})`);
          
          // Pequena celebraÃ§Ã£o visual
          setTimeout(() => setIsAnimating(true), 100);
          setTimeout(() => setIsAnimating(false), 600);
        } else {
          setApiStatus({
            status: 'error',
            message: `Erro ao conectar com a API: ${response.status}`,
            url: API_URL
          });
        }
      } catch (error) {
        setApiStatus({
          status: 'offline',
          message: 'NÃ£o foi possÃ­vel conectar ao servidor',
          error: error.message,
          url: API_URL
        });
        console.error('âŒ Erro ao verificar status da API:', error);
      }
    };

    checkApiStatus();
  }, []);

  // FunÃ§Ã£o para tentar reconectar com a API
  const retryApiConnection = useCallback(() => {
    setApiStatus({
      status: 'checking',
      message: 'Reconectando...',
      url: API_URL
    });
    
    setTimeout(() => {
      checkApiStatus();
    }, 1000);
  }, []);
  
  // FunÃ§Ã£o para verificar status da API
  const checkApiStatus = async () => {
    try {
      const response = await fetch(`${API_URL}/api/health`);
      if (response.ok) {
        const data = await response.json();
        setApiStatus({
          status: 'online',
          env: data.env,
          url: API_URL
        });
        setError(null);
      } else {
        setApiStatus({
          status: 'error',
          message: `Erro ao conectar com a API: ${response.status}`,
          url: API_URL
        });
        setError(`Erro ao conectar com o servidor: ${response.status} ${response.statusText}`);
      }
    } catch (error) {
      setApiStatus({
        status: 'offline',
        message: 'NÃ£o foi possÃ­vel conectar ao servidor',
        error: error.message,
        url: API_URL
      });
      setError(`Falha de conexÃ£o com o servidor: ${error.message}`);
    }
  };

  // FunÃ§Ã£o para limpar os resultados e resetar o estado com animaÃ§Ã£o
  const handleReset = useCallback(() => {
    setIsAnimating(true);
    
    // AnimaÃ§Ã£o de saÃ­da
    setTimeout(() => {
      setSummaries([]);
      setPatientName('');
      setError(null);
      setFiles([]);
      setUploadStatus(null);
      setProcessingStage(null);
      setCurrentProcessingFile(null);
      setProcessedFiles(0);
      setTotalFiles(0);
      
      // Resetar o input de arquivo
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
      
      setIsAnimating(false);
    }, 300);
  }, []);

  // Handlers para drag and drop
  const handleDragOver = useCallback((e) => {
    e.preventDefault();
    setDragOver(true);
  }, []);

  const handleDragLeave = useCallback((e) => {
    e.preventDefault();
    setDragOver(false);
  }, []);

  const handleDrop = useCallback((e) => {
    e.preventDefault();
    setDragOver(false);
    
    const droppedFiles = Array.from(e.dataTransfer.files);
    const pdfFiles = droppedFiles.filter(file => file.type === 'application/pdf');
    
    if (pdfFiles.length === 0 && droppedFiles.length > 0) {
      setError('âš ï¸ Por favor, solte apenas arquivos PDF vÃ¡lidos.');
      setTimeout(() => setError(null), 4000);
      return;
    }
    
    // AnimaÃ§Ã£o ao adicionar arquivos
    setIsAnimating(true);
    setTimeout(() => {
      setFiles(prevFiles => [...prevFiles, ...pdfFiles]);
      setError(null);
      setUploadStatus(null);
      setProcessingStage(null);
      setIsAnimating(false);
    }, 300);
  }, []);

  const handleFileChange = useCallback((e) => {
    const selectedFiles = Array.from(e.target.files);
    setUploadStatus(null);
    setProcessingStage(null);
    
    // Filtrar apenas arquivos PDF
    const pdfFiles = selectedFiles.filter(file => file.type === 'application/pdf');
    
    if (pdfFiles.length === 0 && selectedFiles.length > 0) {
      setError('ðŸ“„ Por favor, selecione apenas arquivos PDF vÃ¡lidos.');
      setTimeout(() => setError(null), 4000);
      return;
    }
    
    // AnimaÃ§Ã£o suave ao adicionar arquivos
    setIsAnimating(true);
    setTimeout(() => {
      setFiles(prevFiles => [...prevFiles, ...pdfFiles]);
      setError(null);
      setIsAnimating(false);
    }, 200);
  }, []);

  // FunÃ§Ã£o para remover um arquivo da lista com animaÃ§Ã£o
  const removeFile = useCallback((index) => {
    setIsAnimating(true);
    setTimeout(() => {
      setFiles(prevFiles => prevFiles.filter((_, i) => i !== index));
      setIsAnimating(false);
    }, 200);
  }, []);

  // FunÃ§Ã£o para limpar todos os arquivos com animaÃ§Ã£o
  const clearAllFiles = useCallback(() => {
    setIsAnimating(true);
    setTimeout(() => {
      setFiles([]);
      if (fileInputRef.current) {
