    }, 200);
  }, []);

  // FunÃ§Ã£o de upload com animaÃ§Ãµes melhoradas
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (files.length === 0) {
      setError('ðŸ“ Por favor, selecione pelo menos um arquivo PDF vÃ¡lido.');
      setTimeout(() => setError(null), 4000);
      return;
    }

    setLoading(true);
    setError(null);
    setIsAnimating(true);
    
    // Status inicial com animaÃ§Ã£o
    setTimeout(() => {
      setUploadStatus({ 
        stage: 'iniciando', 
        message: 'ðŸš€ Iniciando processamento...',
        timestamp: new Date().toLocaleTimeString()
      });
    }, 300);
    
    setProcessingStage('upload');
    setTotalFiles(files.length);
    setProcessedFiles(0);

    // Processar arquivos um por um com feedback visual aprimorado
    const allSummaries = [];
    const fileErrors = [];
    const patientNames = {};

    for (let i = 0; i < files.length; i++) {
      const file = files[i];
      const fileNumber = i + 1;
      setCurrentProcessingFile(file.name);
      
      try {
        const formData = new FormData();
        formData.append('pdf', file);
        
        // Atualizar status com mais detalhes
        setUploadStatus({ 
          stage: 'enviando', 
          message: `ðŸ“¤ Enviando ${fileNumber}/${files.length}: ${file.name.length > 30 ? file.name.substring(0, 30) + '...' : file.name}`,
          progress: Math.round((i / files.length) * 50), // 50% para upload
          timestamp: new Date().toLocaleTimeString()
        });
        
        console.log(`ðŸ“ Enviando arquivo ${file.name} para ${API_URL}/api/upload`);
        const response = await fetch(`${API_URL}/api/upload`, {
          method: 'POST',
          body: formData,
        });
        
        setProcessingStage('processing');
        setUploadStatus({ 
          stage: 'processando', 
          message: `âš™ï¸ Processando ${fileNumber}/${files.length}: Extraindo dados...`,
          progress: Math.round(((i + 0.5) / files.length) * 100),
          timestamp: new Date().toLocaleTimeString()
        });
        
        if (!response.ok) {
          let errorMessage = `Erro ${response.status}: ${response.statusText}`;
          
          try {
            const errorData = await response.json();
            if (errorData.message) {
              errorMessage = errorData.message;
            }
          } catch (jsonError) {
            console.warn('NÃ£o foi possÃ­vel decodificar erro JSON:', jsonError);
          }
          
          throw new Error(`Erro no arquivo ${file.name}: ${errorMessage}`);
        }
        
        const data = await response.json();
        
        // Verificar se temos resultados vÃ¡lidos
        if (!data.summaries || data.summaries.length === 0) {
          throw new Error(`NÃ£o foi possÃ­vel extrair informaÃ§Ãµes do documento ${file.name}.`);
        }
        
        // Armazenar o nome do paciente para este arquivo
        if (data.patientName) {
          patientNames[file.name] = data.patientName;
        }
        
        // Adicionar informaÃ§Ã£o do nome do arquivo e paciente aos resultados
        const fileSummaries = data.summaries.map(summary => ({
          ...summary,
          fileName: file.name,
          patientName: data.patientName,
          processedAt: new Date().toLocaleTimeString()
        }));
        
        // Acumular os resultados
        allSummaries.push(...fileSummaries);
        
        // Se for o primeiro arquivo processado com sucesso, usar seu nome de paciente
        if (i === 0 && data.patientName) {
          setPatientName(data.patientName);
        }
        
        // Atualizar contador de arquivos processados
        setProcessedFiles(i + 1);
        
        // Pequena pausa para feedback visual
        await new Promise(resolve => setTimeout(resolve, 200));
        
      } catch (error) {
        console.error(`âŒ Erro ao processar o arquivo ${file.name}:`, error);
        fileErrors.push({ 
          fileName: file.name, 
          error: error.message,
          timestamp: new Date().toLocaleTimeString()
        });
      }
    }
    
    // Definir o processamento como concluÃ­do com celebraÃ§Ã£o
    setProcessingStage('complete');
    setIsAnimating(false);
    
    if (allSummaries.length > 0) {
      setSummaries(allSummaries);
      
      if (fileErrors.length > 0) {
        // Alguns arquivos foram processados com sucesso
        setUploadStatus({ 
          stage: 'aviso', 
          message: `âš ï¸ Processados ${allSummaries.length} resultados de ${files.length - fileErrors.length}/${files.length} arquivos.`,
          details: `ConcluÃ­do Ã s ${new Date().toLocaleTimeString()}`,
          timestamp: new Date().toLocaleTimeString()
        });
        
        const errorMessage = fileErrors.map(err => 
          `ðŸ“„ ${err.fileName}: ${err.error} (${err.timestamp})`
        ).join('\n');
        setError(`Alguns arquivos nÃ£o puderam ser processados:\n\n${errorMessage}`);
      } else {
        // Todos os arquivos foram processados com sucesso - CELEBRAÃ‡ÃƒO! ðŸŽ‰
        setUploadStatus({ 
          stage: 'sucesso', 
          message: `ðŸŽ‰ Processamento concluÃ­do com sucesso! ${allSummaries.length} resultados de ${files.length} arquivos.`,
          details: `Finalizado Ã s ${new Date().toLocaleTimeString()}`,
          timestamp: new Date().toLocaleTimeString()
        });
        setError(null);
        
        // Ativar celebraÃ§Ã£o visual
        setShowCelebration(true);
        setTimeout(() => setShowCelebration(false), 3000);
      }
    } else {
      // Nenhum arquivo foi processado com sucesso
      setUploadStatus({ 
        stage: 'erro', 
        message: 'âŒ Nenhum resultado foi extraÃ­do dos arquivos.',
        details: `Falha Ã s ${new Date().toLocaleTimeString()}`,
        timestamp: new Date().toLocaleTimeString()
      });
      
      const errorMessage = fileErrors.map(err => 
        `ðŸ“„ ${err.fileName}: ${err.error} (${err.timestamp})`
      ).join('\n');
      setError(`Falha ao processar todos os arquivos:\n\n${errorMessage}`);
    }
    
    setLoading(false);
    setCurrentProcessingFile(null);
  };

  // FunÃ§Ã£o para obter descriÃ§Ã£o amigÃ¡vel do mÃ©todo de extraÃ§Ã£o
  const getMethodDescription = useCallback((method) => {
    const descriptions = {
      'direto': 'âš¡ processamento direto',
      'desprotegido': 'ðŸ”“ remoÃ§Ã£o de proteÃ§Ã£o',
