  // FunÃ§Ã£o para extrair apenas os exames sem seÃ§Ãµes ou formataÃ§Ã£o extra
  const extractOnlyExams = useCallback(() => {
    let allExams = [];
    
    summaries.forEach((summary) => {
      const lines = summary.content.split('\n');
      
      lines.forEach((line) => {
        // Filtrar linhas que comeÃ§am com SÃ‰RIE, HEMOGRAMA, EXAMES ou Paciente:
        if (line.trim() && 
