        textAreaRef.current.style.background = 'linear-gradient(135deg, #fce8e6, #fff5f5)';
        textAreaRef.current.style.color = '#c5221f';
        
        setTimeout(() => {
          textAreaRef.current.value = originalText;
          textAreaRef.current.style.background = '';
