  }, []);

  // Calcular o progresso total do processamento
  const calculateProgress = useCallback(() => {
    if (totalFiles === 0) return 0;
    return Math.round((processedFiles / totalFiles) * 100);
  }, [processedFiles, totalFiles]);

  // FunÃ§Ã£o debounced para search/filter (preparaÃ§Ã£o para funcionalidades futuras)
  const debouncedSearch = useDebounce((searchTerm) => {
    // Implementar busca nos resultados se necessÃ¡rio
    console.log('Buscando por:', searchTerm);
  }, 300);

  return (
    <div className={`App ${isAnimating ? 'animating' : ''} ${showCelebration ? 'celebrating' : ''}`}>
