                ></div>
              </div>
              <div className="progress-status">
                {currentProcessingFile ? (
                  <>
                    {processingStage === 'upload' ? 'ðŸ“¤' : 'âš™ï¸'} 
                    {` ${processingStage === 'upload' ? 'Enviando' : 'Processando'}: `}
                    {currentProcessingFile.length > 25 ? 
                      `${currentProcessingFile.substring(0, 25)}...` : 
                      currentProcessingFile
                    }
                    {` (${processedFiles + 1}/${totalFiles})`}
                  </>
                ) : (
                  <>
                    {processingStage === 'upload' ? 'ðŸ“¤ Enviando arquivo...' : 'âš™ï¸ Processando documento...'}
                  </>
                )}
              </div>
            </div>
          )}
          
          {/* Status de upload melhorado */}
          {uploadStatus && (
            <div className={`upload-status ${uploadStatus.stage}`}>
              <span className="status-icon">
                {uploadStatus.stage === 'sucesso' ? 'ðŸŽ‰' : 
