import React, { useState, useRef } from 'react';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [summaries, setSummaries] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [viewMode, setViewMode] = useState('cards'); // 'cards' ou 'text'
  const textAreaRef = useRef(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/pdf') {
      setFile(selectedFile);
      setError(null);
    } else {
      setFile(null);
      setError('Por favor, selecione um arquivo PDF válido.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setError('Por favor, selecione um arquivo PDF válido.');
      return;
    }

    const formData = new FormData();
    formData.append('pdf', file);

    setLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:5000/api/upload', {
        method: 'POST',
        body: formData,
      });
      
      if (!response.ok) {
        throw new Error('Erro ao processar o documento');
      }
      
      const data = await response.json();
      
      // Combinar todos os resultados em um único objeto
      const combinedResults = combineResults(data.summaries);
      setSummaries(combinedResults);
    } catch (error) {
      console.error('Erro ao enviar o arquivo:', error);
      setError(error.message || 'Erro ao processar o documento. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  // Função para combinar todos os resultados e organizá-los por seção
  const combineResults = (summaries) => {
    // Objeto para armazenar os resultados agrupados por seção
    const sectionMap = {};
    let currentSection = "Resultados";
    
    // Processar cada página e seus conteúdos
    summaries.forEach(summary => {
      const lines = summary.content.split('\n');
      
      lines.forEach(line => {
        if (line.trim() === '') return;
        
        // Se for um título de seção, atualize a seção atual
        if (isSectionTitle(line)) {
          currentSection = line.trim();
          if (!sectionMap[currentSection]) {
            sectionMap[currentSection] = [];
          }
        } 
        // Caso contrário, adicione a linha à seção atual
        else {
          if (!sectionMap[currentSection]) {
            sectionMap[currentSection] = [];
          }
          sectionMap[currentSection].push(line.trim());
        }
      });
    });
    
    // Converter o mapa de seções em um array de objetos com nome da seção e resultados
    return Object.keys(sectionMap).map(section => ({
      section: section,
      results: sectionMap[section]
    }));
  };

  // Função para copiar texto para a área de transferência
  const copyToClipboard = () => {
    if (textAreaRef.current) {
      textAreaRef.current.select();
      document.execCommand('copy');
      // Mostrar uma mensagem de sucesso
      alert('Resultados copiados para a área de transferência!');
    }
  };

  // Gerar texto formatado de todos os resultados para cópia
  const getFormattedTextForCopy = () => {
    let formattedText = 'INSTITUTO PAULO GODOI - RESULTADOS DE EXAMES\n';
    formattedText += '=============================================\n\n';
    formattedText += `Data: ${new Date().toLocaleDateString('pt-BR')}\n\n`;
    
    summaries.forEach((sectionData) => {
      formattedText += `\n${sectionData.section}\n${'='.repeat(sectionData.section.length)}\n\n`;
      
      sectionData.results.forEach(line => {
        formattedText += `${line}\n`;
      });
      
      formattedText += '\n';
    });
    
    formattedText += '\nDocumento gerado pelo Sistema do Instituto Paulo Godoi\n';
    
    return formattedText;
  };

  // Identifica se uma linha é um título de seção (como SÉRIE ERITROCITÁRIA)
  const isSectionTitle = (line) => {
    return line.match(/^SÉRIE|^HEMOGRAMA|^EXAMES/i) !== null;
  };

  // Função para formatar as linhas de resultados
  const formatResultLine = (line) => {
    // Verifica se a linha tem o formato "Exame: Resultado | Referência: Valor"
    const parts = line.split('|');
    
    if (parts.length === 2) {
      // Separa o resultado do exame e a referência
      const resultPart = parts[0].trim();
      const referencePart = parts[1].trim();
      
      // Extrai o nome do exame e o valor
      const resultMatch = resultPart.match(/^(.+):\s*(.+)$/);
      
      if (resultMatch) {
        const examName = resultMatch[1].trim();
        const examValue = resultMatch[2].trim();
        
        // Verifica se o valor tem '/' indicando resultado duplo
        if (examValue.includes('/')) {
          const [percentValue, absoluteValue] = examValue.split('/').map(v => v.trim());
          
          return (
            <div className="exam-result">
              <span className="exam-name">{examName}:</span> 
              <span className="exam-value dual-value">
                <span className="percent-value">{percentValue}</span>
                <span className="divider">/</span>
                <span className="absolute-value">{absoluteValue}</span>
              </span>
              <span className="exam-reference"> | {referencePart}</span>
            </div>
          );
        } else {
          return (
            <div className="exam-result">
              <span className="exam-name">{examName}:</span> 
              <span className="exam-value">{examValue}</span>
              <span className="exam-reference"> | {referencePart}</span>
            </div>
          );
        }
      }
    }
    
    // Fallback para linhas que não têm o formato esperado
    return <div className="exam-result">{line}</div>;
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="institute-logo">
          <span className="logo-text">Instituto Paulo Godoi</span>
        </div>
        <h1>Extrator de Resultados de Exames</h1>
        <p className="subtitle">Sistema interno para processamento de laudos laboratoriais</p>
      </header>
      <main>
        <div className="uploader-container">
          <form onSubmit={handleSubmit}>
            <div className="file-input-container">
              <input
                type="file"
                id="pdf-upload"
                onChange={handleFileChange}
                accept="application/pdf"
              />
              <label htmlFor="pdf-upload" className="file-label">
                {file ? file.name : 'Escolher arquivo PDF de exames'}
              </label>
            </div>
            <button 
              type="submit" 
              className="upload-button"
              disabled={!file}
            >
              Extrair Resultados
            </button>
          </form>
        </div>

        {loading && <p className="loading">Processando o documento, por favor aguarde...</p>}
        {error && <p className="error">Erro: {error}</p>}

        {summaries.length > 0 && (
          <div className="view-mode-toggle">
            <button 
              className={`toggle-button ${viewMode === 'cards' ? 'active' : ''}`}
              onClick={() => setViewMode('cards')}
            >
              Visualização em Cards
            </button>
            <button 
              className={`toggle-button ${viewMode === 'text' ? 'active' : ''}`}
              onClick={() => setViewMode('text')}
            >
              Visualização para Cópia
            </button>
          </div>
        )}

        <div className="summary-container">
          {summaries.length === 0 ? (
            <p className="empty-message">Os resultados dos exames aparecerão aqui.</p>
          ) : viewMode === 'cards' ? (
            <>
              <h2>Resultados dos Exames</h2>
              <div className="summaries-list">
                {summaries.map((sectionData, index) => (
                  <div key={index} className="summary-card">
                    <div className="card-header">
                      <h3>{sectionData.section}</h3>
                    </div>
                    <div className="summary-content">
                      {sectionData.results.map((line, i) => (
                        <div key={i} className="result-line">
                          {formatResultLine(line)}
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
              <div className="export-container">
                <button onClick={() => window.print()} className="export-button">
                  <i className="print-icon">🖨️</i> Imprimir Resultados
                </button>
              </div>
            </>
          ) : (
            <div className="text-view-container">
              <h2>Resultados para Cópia</h2>
              <p className="copy-instructions">Todos os resultados em formato de texto para copiar e colar:</p>
              <div className="text-area-container">
                <textarea
                  ref={textAreaRef}
                  className="results-text-area"
                  value={getFormattedTextForCopy()}
                  readOnly
                />
                <button onClick={copyToClipboard} className="copy-button">
                  <i className="copy-icon">📋</i> Copiar para Área de Transferência
                </button>
              </div>
            </div>
          )}
        </div>
      </main>
      <footer>
        <p>© 2025 - Instituto Paulo Godoi - Sistema de Processamento de Exames</p>
        <p className="contact-info">Contato: (XX) XXXX-XXXX | contato@institutopaulogodoi.com.br</p>
      </footer>
    </div>
  );
}

export default App;
