// client/src/components/PdfUploader.js
import React, { useState } from 'react';
import axios from 'axios';
import './PdfUploader.css';

const PdfUploader = ({ onSummariesUpdate, setLoading, setError }) => {
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/pdf') {
      setFile(selectedFile);
      setError(null);
    } else {
      setFile(null);
      setError('Por favor, selecione um arquivo PDF v치lido.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setError('Por favor, selecione um arquivo PDF v치lido.');
      return;
    }

    const formData = new FormData();
    formData.append('pdf', file);

    setLoading(true);
    setError(null);

    try {
      const response = await axios.post('http://localhost:5000/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      onSummariesUpdate(response.data.summaries);
    } catch (error) {
      console.error('Erro ao enviar o arquivo:', error);
      setError(error.response?.data?.message || 'Erro ao processar o documento. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="uploader-container">
      <form onSubmit={handleSubmit}>
        <div className="file-input-container">
          <input
            type="file"
            id="pdf-upload"
            onChange={handleFileChange}
            accept="application/pdf"
          />
          <label htmlFor="pdf-upload" className="file-label">
            {file ? file.name : 'Escolher arquivo PDF'}
          </label>
        </div>
        <button 
          type="submit" 
          className="upload-button"
          disabled={!file}
        >
          Gerar Resumos
        </button>
      </form>
    </div>
  );
};

export default PdfUploader;
